import React, { useState, useEffect } from 'react';
import './App.css';

const Div = () => {
  const [number, setNumber] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (isAnimating) {
      const interval = setInterval(() => {
        setNumber(Math.floor(Math.random() * 49 + 1));
      }, 100);

      setTimeout(() => {
        clearInterval(interval);
        setIsAnimating(false);
      }, 3000);
    }
  }, [isAnimating]);

  const handleClick = () => {
    setIsAnimating(true);
  }

  return (
    <div 
      onClick={handleClick}
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {number}
    </div>
  );
}

const Siatka = () => {
  const grid = [];

  for (let i = 0; i < 6; i++) {
    grid.push(
      <Div
        key={i}
      />
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
    </div>
  );
};

export default Siatka;
