import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";


const URL = 'http://localhost:3001';

function Uzytkownicy() {

  let navigate = useNavigate();
  const token = localStorage.getItem('token');

  if (!token || !role || role !== 'admin') {
    navigate("/");  // Przekierowanie do strony głównej, jeśli użytkownik nie jest adminem lub nie jest zalogowany
  }


  const [data, setData] = useState([]); 
  const [page, setPage] = useState(1);

  const fetchUsers = async (pageNumber) => {
    try {
      const response = await fetch(`${URL}/uzytkownicy?page=${pageNumber}`, {
        method: 'post',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json();
      console.log(data);

      setData(data);
      return;
    } catch (error) {
      console.log(`Niepoprawne dane logowania: ${error.message}`);
    }
  };

  const nextPage = () => setPage(page + 1);
  const prevPage = () => setPage(page > 1 ? page - 1 : page);

  return (
    <div>
      <button onClick={() => fetchUsers(page)}>Szukaj</button>

      <button onClick={() => { prevPage(); fetchUsers(page); }}>Poprzednia</button>
      <button onClick={() => { nextPage(); fetchUsers(page); }}>Następna</button>

      {data && (
        <div className="vh80 divWithScroll">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Age</th>
                <th>OPT</th>
              </tr>
            </thead>
            <tbody>
              {data.map((person, index) => (
                <tr key={index}>
                  <td>{person.id}</td>
                  <td>{person.user}</td>
                  <td>{person.age}</td>

                  <td>
                    <button>Edytuj</button>
                    <button>Usuń</button>
                    <button>Zablokuj</button>
                    <select>
                      <option>Dodaj karte</option>
                      <option>RFID</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Uzytkownicy;
