import React, { useState, useEffect } from 'react';
import './App.css';

const generateShuffledNumbers = (min, max) => {
  let numbers = Array.from({length: max - min + 1}, (_, i) => i + min);
  for(let i = numbers.length - 1; i > 0; i--){
    const j = Math.floor(Math.random() * (i + 1));
    [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
  }
  return numbers;
};

const Div = ({ startAnimation, number, animationDuration, minNumber, maxNumber }) => {
  const [displayedNumbers, setDisplayedNumbers] = useState(generateShuffledNumbers(minNumber, maxNumber));

  useEffect(() => {
    const shuffledNumbers = generateShuffledNumbers(minNumber, maxNumber);
    if (startAnimation) {
      setDisplayedNumbers(shuffledNumbers);
    } else {
      setDisplayedNumbers([
        shuffledNumbers[0],
        number, 
        shuffledNumbers[0]
      ]);
    }
  }, [startAnimation, number, minNumber, maxNumber]);

  return (
    <div style={{ margin: '20px' }}>
      <input 
        type="range" 
        min="0.1" 
        max="5.0" 
        step="0.1" 
        value={animationDuration} 
        onChange={handleSliderChange} 
        orient="vertical"
        style={{writingMode: 'bt-lr'}}
      />
      <div style={{ width: '50px', height: '50px', border: '1px solid black', backgroundColor: '#eee', display: 'flex', justifyContent: 'center', alignItems: 'center', overflow: 'hidden' }}>
        <div 
          className={startAnimation ? 'number-spin' : ''} 
          style={{ position: 'relative', animationDuration: `${animationDuration}s` }}
        >
          {displayedNumbers.map((num, index) => (
            <div key={index} style={{ marginBottom: '2px' }}>{num}</div>
          ))}
        </div>
      </div>
    </div>
  );
};

const Siatka = () => {
  const [startAnimation, setStartAnimation] = useState(false);
  const [numbers, setNumbers] = useState([]);
  const [animationDurations, setAnimationDurations] = useState([1, 1, 1, 1, 1, 1]);
  const [minNumber, setMinNumber] = useState(1);
  const [maxNumber, setMaxNumber] = useState(49);
  const grid = [];

  const handleClick = () => {
    const nums = new Set();
    while(nums.size < 6) {
      nums.add(Math.floor(Math.random() * (maxNumber - minNumber + 1) + minNumber));
    }
    setNumbers([...nums]);
    setStartAnimation(true);
  }

  useEffect(() => {
    if (!startAnimation) return;

    let timeout;
    for (let i = 0; i < 6; i++) {
      timeout = setTimeout(() => {
        setStartAnimation(i < 5);
      }, animationDurations[i] * 1000);
    }

    return () => clearTimeout(timeout);
  }, [startAnimation, animationDurations]);

  for (let i = 0; i < 6; i++) {
    const handleSliderChange = (e) => {
      setAnimationDurations(animationDurations.map((duration, index) => {
        if (index === i) {
          return Number(e.target.value);
        } else {
          return duration;
        }
      }));
    }

    grid.push(
      <Div
        key={i}
        startAnimation={startAnimation}
        number={numbers[i]}
        animationDuration={animationDurations[i]}
        minNumber={minNumber}
        maxNumber={maxNumber}
        handleSliderChange={handleSliderChange}
      />
    );
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>Losowanie Lotto</h1>
      <div style={{ display: 'flex' }}>
        {grid}
      </div>
      <button onClick={handleClick}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
